let uuid      = require('uuid');
let debug = require('debug')('setcookies');

async function setCookies (req, h, options) {
    let authCred = req.auth.credentials;
    debugger;
    req.log('setcookie',authCred);
    if (authCred != null && req.auth.error != null) {
        debug('logon failed');
        return { status: false, error: req.auth.error };
    }
        
    // create a cookie(sid) and save credentials in cache
    const sid = uuid.v4();

    let credentials = {
        token       : authCred.token,
        refreshToken: authCred.refreshToken,
        sid         : sid,
        user_name   : '',
        credentials : authCred
    };

    if (options.useToken  === true) {
        options.allAppEnv.LOGONPAYLOAD.token = authCred.token;
        options.allAppEnv.LOGONPAYLOAD.tokenType = 'bearer';
    }
    
    authCred.sid = sid;
    await req.server.app.cache.set(sid, credentials, 0);
    req.cookieAuth.set({ sid });
    
    debug('authcred query:', authCred.query);
  
    let redirect = (authCred.query != null && authCred.query.next != null) ? credentials.query.next : null;
    if (redirect == null) {
        redirect = (options.redirect != null) ? options.redirect : null;
    }

    return { status: true, error: null , redirect: redirect};
}

export default setCookies;

